INSERT INTO `topic` VALUES ('2', '6', ' 社交网络系统', '<p><strong>实现一个B/S结构的社交网络系统。后台数据库使用GraphDB。</strong></p>', '简单的社交网络实现', 'J2EE、GraphDB', '2', '1', '2', '0131121', '0131122734', '0', '1', '3', '2016-06-04 18:38:48', '1', '3', '0');
INSERT INTO `topic` VALUES ('3', '4', '基于SSH架构人力资源管理系统设计及实现', '<p>实现MVC设计模式的struts+spring+hibernate架构的人力资源管理系统。</p><p>人力资源管理系统的具体目标如下：</p><table><tbody><tr><td width=\"984\" valign=\"top\">1)对企业人力资源管理的基本信息进行管理；</td></tr><tr><td width=\"984\" valign=\"top\">2)管理企业的员工信息（即人事管理功能）；</td></tr><tr><td width=\"984\" valign=\"top\">3)实现为个人提供网络工作平台的功能；</td></tr><tr><td width=\"984\" valign=\"top\">4)实现企业的应聘信息的管理功能；</td></tr><tr><td width=\"984\" valign=\"top\">5)实现企业员工培训的一系列相关信息的管理；</td></tr><tr><td width=\"984\" valign=\"top\">6)员工薪酬信息管理；</td></tr><tr><td width=\"984\" valign=\"top\">7)系统用户信息的管理。</td></tr></tbody></table><p><br/></p>', '', 'J2EE', '4', '1', '2', '0131121', null, '0', '0', '0', '2016-06-05 10:31:09', '0', '3', '0');
INSERT INTO `topic` VALUES ('4', '7', '网上学生成绩管理系统设计', '<p>修改的内容</p>', '', 'ASP.NET', '3', '1', '2', null, '0131122589', '1', '0', '1', '2016-06-08 00:23:52', '1', '2', '0');
INSERT INTO `topic` VALUES ('6', '2', '移动网上营业厅平台设计及实现', '<p>利用JSP技术开发设计一个移动网上营业厅平台，本项目主要是模拟一个B/S模式的小型移动网上营业厅，用户可以通过浏览器访问。基本功能如下：</p><p>&nbsp; &nbsp;1：游客（即非移动客户）：可以查询近期移动相关业务信息，以考虑是否成为移动客户。</p><p>&nbsp; &nbsp;2：移动客户：可在网上可实现在线充值，各类业务查询，业务开通及关闭，修改用户资料，本月及上月的消费情况查询，账户余额及有效期查询，已开通业务查询，发送客户意见，服务打分等功能。</p><p>&nbsp; &nbsp;3：营业员：是前台与客户直接接触的工作人员，所以他可实现前台客户各项业务受理，新用户入网，客户意见查看处理等操作。</p><p>&nbsp; &nbsp;4：营业厅经理：不与客户直接接触，他主要负责后台事务的管理工作。如业务信息的增删改，员工信息的增、删、改，以及客户意见的管理等。</p><p><br/></p>', 'OK', '', '3', '1', '1', '0131121', '0131122464', '1', '1', '1', '2016-06-08 14:58:05', '1', '3', '0');
